*{
  margin: 0;
  padding: 0;
  box-sizing: border-box;

  /* --bg-color: #141517; */
  --bg-color: #2e2e2e;
  /* --text-color: #FDFCF2; */
  --text-color: #F7EEDA;
  /* --yellow-color: #E2A84D; */
  --yellow-color: #FFA700;
  --green-color: #44BE8A;
  --bg-nav-color: #F7EEDA;
  --bg-x-color: #E6E0D3;
}

body{
  background: var(--bg-color);
}

#app {
  font-family: 'General Sans', sans-serif;
  color: var(--text-color);
  overflow: hidden;

  display: flex;
  flex-direction: column;
  min-height: 100%;
}

.nav{
  width: 100%;
  background: var(--bg-nav-color);
  /* border-radius: .3rem; */
  position: fixed;
  bottom: 0;
  left: 50%;
  transform: translateX(-50%);
  padding: .5rem;
  display: grid;
  grid-template-columns: 1fr 1fr ;
  justify-items: center;
  z-index: 9999;
  box-shadow: 0px -8px 18px -1px rgba(46,46,46,0.20);
}

#nav a {
  font-size: 1rem;
  font-weight: 400;
  text-decoration: none;
  color: var(--bg-color);
  width: 100%;
  height: 100%;
  text-align: center;
  padding: .7rem 0;
  border-radius: .3rem;
}

#nav a.router-link-exact-active {
  /* font-weight: 600; */
  color: var(--text-color);
  background: var(--bg-color);
  transition: all ease .5s;  

}



h1{
  font-size: 4rem;
  font-weight: 600;
  line-height: 4rem;
  margin: 0 0 3rem 0;
}

h2{
  font-size: 2rem;
  margin: 0 0 2rem 0;
}

/* TRANSITION CODE FROM NICOOPRAT – https://codesandbox.io/s/zq5mw2zk9x?file=/src/App.vue:1351-1806 */

main {
  min-height: 100%;
  display: grid;
  grid-template: "main";
  flex: 1;
  position: relative;
  overflow: hidden;
  background-color: var(--bg-color);
}

main > * {
  grid-area: main; /* Transition: make sections overlap on same cell */
  flex: 1 1 auto;
  background-color: var(--bg-color);
  position: relative;
  /* height: 100vh; To be fixed */
  min-height: 100vh;
}

main > :first-child {
  z-index: 1; /* Prevent flickering on first frame when transition classes not added yet */
}

/* Transitions */

.next-leave-to {
  animation: leaveToLeft 500ms both cubic-bezier(0.165, 0.84, 0.44, 1);
  z-index: 0;
}

.next-enter-to {
  animation: enterFromRight 500ms both cubic-bezier(0.165, 0.84, 0.44, 1);
  z-index: 1;
}

.prev-leave-to {
  animation: leaveToRight 500ms both cubic-bezier(0.165, 0.84, 0.44, 1);
  z-index: 1;
}

.prev-enter-to {
  animation: enterFromLeft 500ms both cubic-bezier(0.165, 0.84, 0.44, 1);
  z-index: 0;
}

@keyframes leaveToLeft {
  from {
    transform: translateX(0);
  }
  to {
    transform: translateX(-25%);
    filter: brightness(0.5);
  }
}

@keyframes enterFromLeft {
  from {
    transform: translateX(-25%);
    filter: brightness(0.5);
  }
  to {
    transform: translateX(0);
  }
}

@keyframes leaveToRight {
  from {
    transform: translateX(0);
  }
  to {
    transform: translateX(100%);
  }
}

@keyframes enterFromRight {
  from {
    transform: translateX(100%);
  }
  to {
    transform: translateX(0);
  }
}
